import{_ as t,c as o,o as e,a as u}from"./app.a7049be0.js";const b='{"title":"\u76F8\u7530\u304F\u3072\u3092\u7684\u524D\u8A00","description":"","frontmatter":{},"headers":[],"relativePath":"chapter-1/index.md"}',a={},p=u('<h1 id="\u76F8\u7530\u304F\u3072\u3092\u7684\u524D\u8A00" tabindex="-1">\u76F8\u7530\u304F\u3072\u3092\u7684\u524D\u8A00 <a class="header-anchor" href="#\u76F8\u7530\u304F\u3072\u3092\u7684\u524D\u8A00" aria-hidden="true">#</a></h1><p>\u8FD9\u672C\u4E66\uFF0C\u8BE6\u8BE6\u7EC6\u7EC6\u5199\u5C3D\u81EA\u6740\u7684\u65B9\u6CD5\u3002</p><p>\u8FD9\u672C\u4E66\u4E0D\u662F\u5E38\u81EA\u6740\u8005\u7684\u8BB0\u5F55\u62A5\u544A\uFF0C\u4E5F\u4E0D\u662F\u60F3\u8BF4\u660E\u6709\u5173\u81EA\u6740\u7684\u7406\u7531\u3002\u867D\u7136\u53EF\u4EE5\u5C06\u5B83\u5F53\u6210\u662F\u4E00\u672C\u6742\u8BB0\u4E66\u6765\u9605\u8BFB\uFF0C\u4E0D\u8FC7\u6574\u672C\u4E66\u7684\u8D70\u5411\u662F\u671D\u7740&quot;\u5982\u4F55\u81EA\u6740&quot;\u7684\u65B9\u5411\u8FDB\u884C\u3002</p><p>\u5570\u5570\u55E6\u55E6\u7684\u5F00\u573A\u767D\uFF0C\u53EF\u80FD\u60A8\u5DF2\u7ECF\u538C\u70E6\u4E86\u3002</p><p>\u4E0D\u77E5\u9053\u4ECE\u591A\u4E45\u4EE5\u524D\u5F00\u59CB&quot;\u4E3A\u4EC0\u4E48\u5E74\u8F7B\u4EBA\u8981\u8D70\u5411\u6B7B\u4EA1?&quot;\u8FD9\u4E2A\u8BDD\u9898\u5C31\u4E0D\u65AD\u53CD\u590D\u518D\u53CD\u590D\u5730\u88AB\u8C08\u8BBA\u7740\u3002\u90A3\u65F6\uFF0C\u6BD4\u65B9\u8BF4\u4E03\u5341\u5E74\u4EE3\uFF0C\u6240\u5F97\u5230\u7684\u7ED3\u8BBA\u662F&quot;\u865A\u65E0\u4E3B\u4E49&quot;\u3001&quot;\u4E0D\u6B22\u5E74\u4EE3&quot;\u7B49\u7B49\u3002\u7136\u800C\uFF0C\u50CF\u662F&quot;\u4E3A\u4EC0\u4E48\u4E0D\u53EF\u4EE5\u81EA\u6740?&quot;\u3001&quot;\u4E3A\u4EC0\u4E48\u4E00\u5B9A\u8981\u6D3B\u7740?&quot;\u8FD9\u7C7B\u7684\u95EE\u9898\uFF0C\u5374\u59CB\u7EC8\u6CA1\u6709\u4EFB\u4F55\u89E3\u7B54\u3002</p><p>\u8A00\u5F52\u6B63\u4F20\uFF0C\u76EE\u524D\u9700\u8981\u7684\u662F\u4E00\u672C\u80FD\u8BA9&quot;\u81EA\u6740&quot;\u4ED8\u8BF8\u5B9E\u73B0\u7684\u4E66\u3002</p><p>\u6709\u5173\u8FD9\u6837\u7684\u4E66\uFF0C\u5341\u5E74\u524D\u51FA\u7248\u7684&quot;\u81EA\u6740\u7684\u65B9\u6CD5&quot;\uFF0C\u51E0\u4E4E\u4E5F\u53EA\u662F\u5199\u4E9B\u62C9\u62C9\u6742\u6742\u7684\u5185\u5BB9\uFF0C\u4EE4\u4EBA\u70E6\u817B\u4E4B\u81F3\u3002\u73B0\u5728\u5E94\u8BE5\u77E5\u9053\u7684\u662F\uFF0C\u7EAF\u7CB9\u7684\u81EA\u6740\u65B9\u6CD5\u3002</p><p>\u5728\u7F8E\u56FD\uFF0C\u53EA\u6709\u4E00\u90E8\u53EF\u4EE5\u8FDB\u884C\u5B89\u4E50\u6B7B\u7684\u81EA\u6740\u88C5\u7F6E\uFF0C\u662F\u7531\u4E00\u4F4D\u5B66\u8005\u53D1\u660E\u521B\u9020\u7684\u3002(\u6848\u4F8B30)\u800C\u672C\u4E66\u5C06\u662F\u65E5\u672C\u552F\u4E00\u7684\u4E00\u672C&quot;\u4EE5\u8BED\u8A00\u6587\u5B57\u4E3A\u5DE5\u5177\u7684\u81EA\u6740\u88C5\u7F6E&quot;\u3002</p><p>\u8C08\u5230\u8FD9\u91CC\uFF0C\u5F88\u60F3\u9A6C\u4E0A\u4ECE\u5403\u836F\u81EA\u6740\u7684\u65B9\u6CD5\u4ECB\u7ECD\u8D77\uFF0C\u4E0D\u8FC7\u4E3A\u4E86\u8BA9&quot;\u73B0\u5728\u4E3A\u4EC0\u4E48\u8981\u81EA\u6740?&quot;\u8FD9\u4E2A\u95EE\u9898\u66F4\u660E\u767D\u4E9B\uFF0C\u4EE5\u53CA\u5176\u5B83\u79CD\u79CD\u5546\u4E1A\u4E0A\u7684\u7406\u7531\uFF0C\u4E0D\u5F97\u4E0D\u5148\u5199\u4E00\u4E9B\u62C9\u62C9\u6742\u6742\u7684\u4E1C\u897F\u3002</p><div class="tooltip"><h2>\u300AChernobyl\u300B</h2><span class="tooltiptext">\u5C0F\u6797\u7950\u4ECB\u7684\u6B4C\u66F2</span></div><p>&quot;\u2026\u2026\u6211\u5728\u7B49\u5F85\u65F6\u673A\uFF0C\u51C6\u5907&quot;\u5927\u663E\u8EAB\u624B&quot;\u3002\u4E8C\u5341\u5E74\u524D\u53D1\u751F\u5B66\u751F\u66B4\u52A8\u65F6\uFF0C\u5927\u5BB6\u66FE\u4EE5\u4E3A&quot;\u5389\u5BB3\u4EBA\u7269\u8981\u51FA\u73B0\u4E86\uFF01&quot;\u963F\u6CE2\u7F57\u53F7\u767B\u9646\u6708\u7403\u3001\u77F3\u6CB9\u5371\u673A\u3001\u82CF\u8054\u4FB5\u7565\u67D0\u4E2A\u56FD\u5BB6\u3001\u662D\u548C\u5E74\u4EE3\u5373\u5C06\u7ED3\u675F\uFF0C\u6211\u60F3&quot;\u8FD9\u6B21\u7684\u4E3E\u52A8\u5C06\u4F1A\u60CA\u5929\u52A8\u5730&quot;\u3002\u7136\u800C\u5374\u53EA\u662F\uFF13\u7EA7\u9707\u5EA6\uFF0C\u53EA\u4E0D\u8FC7\u5012\u4E0B\u4E00\u9762\u5899\u800C\u5DF2\u3002\u5B66\u751F\u5F7C\u6B64\u5BF9\u89C6\uFF0C\u7B11\u7740\u8BF4&quot;\u592A\u68D2\u4E86\uFF01&quot;\uFF0C\u6D3B\u52A8\u5373\u544A\u7ED3\u675F\u3002\u2026\u2026&quot;(\u8282\u5F55\u81EA\u77E5\u4E0A\u5BFF\u300A\u9ECE\u660E\u540E\u8BB0\u300B)</p><p>\u516B\u5341\u5E74\u4EE3\u5373\u5C06\u7ED3\u675F\u65F6\uFF0C\u66FE\u7ECF\u6380\u8D77\u4E00\u9635&quot;\u4E16\u754C\u672B\u65E5\u6F6E&quot;\u3001&quot;\u5371\u9669\u8BDD\u9898&quot;\uFF0C\u6700\u53D7\u559C\u7231\u7684\u4E50\u56E2\u5531\u7740Chernobyl\u7684\u6B4C\uFF0C\u5C0F\u5B69\u5B50\u7684\u73A9\u7B11\u8BDD\u5168\u90FD\u5145\u6EE1\u6B7B\u4EA1\u7684\u5473\u9053\uFF0C\u5C11\u5973\u4E3A\u4E86\u51C6\u5907\u8FCE\u63A5\u4E16\u754C\u5927\u6218\u800C\u5F00\u59CB\u5BFB\u627E\u540C\u4F34\u3002\u6211\u4EEC\u5219\u4E3A&quot;\u5389\u5BB3\u4EBA\u7269\u8981\u51FA\u73B0\u4E86\uFF01&quot;\u3001&quot;\u4E5F\u8BB8\u660E\u5929\u4F1A\u662F\u4E16\u754C\u672B\u65E5\uFF01&quot;\u800C\u5174\u594B\u4E0D\u5DF2\u3002</p><p>\u7136\u800C\uFF0C\u4E16\u754C\u672B\u65E5\u5E76\u6CA1\u6709\u5230\u6765\u3002\u539F\u5B50\u5F39\u59CB\u7EC8\u6CA1\u6709\u7206\u53D1\uFF0C\u5168\u9762\u6838\u6218\u4E89\u7684\u68A6\u4E5F\u65E9\u5DF2\u6D88\u5931\u7684\u65E0\u5F71\u65E0\u8E2A\u3002\u516B\u5341\u5E74\u4EE3\u7684\u9769\u547D\u5BB6\uFF0C\u5F7B\u5E95\u7684\u4F53\u9A8C\u5230\u632B\u6298\u611F\u3002</p><p>\u6700\u540E\u5927\u5BB6\u7EC8\u4E8E\u660E\u767D\uFF0C&quot;\u5927\u663E\u8EAB\u624B&quot;\u662F\u4E0D\u53EF\u80FD\u5B9E\u73B0\u7684\uFF0C\u4E8C\u5341\u4E8C\u4E16\u7EAA\u4E00\u5B9A\u4F1A\u6765\u3002(\u5F53\u7136\uFF0C\u4E8C\u5341\u4E00\u4E16\u7EAA\u5373\u5C06\u6765\u4E34\uFF0C\u56E0\u4E3A\u4E0D\u4F1A\u6709\u6240\u8C13\u7684\u4E16\u754C\u5927\u6218\u3002)\u4E16\u754C\u7EDD\u5BF9\u4E0D\u4F1A\u51FA\u73B0\u672B\u65E5\u3002\u53EA\u662F\u7A0D\u7A0D\u63A5\u9047\u5230&quot;\u5F02\u754C&quot;\u53CA&quot;\u5916\u9762&quot;\uFF0C\u5E76\u65E0\u6CD5\u5F97\u5230\u6EE1\u8DB3\u3002\u5982\u679C\u5E0C\u671B\u6709\u66F4\u5927\u7684\u523A\u6FC0\uFF0C\u5982\u679C\u771F\u7684\u5E0C\u671B\u4E16\u754C\u8D70\u5230\u672B\u65E5\uFF0C\u9664\u975E\u662F\u505A&quot;\u90A3\u4EF6\u4E8B&quot;\u3002</p><div class="tooltip"><h2>\u300AA Long Vacation\u300B</h2><span class="tooltiptext">\u4E2D\u91CE\u7167\u592B\u521B\u4F5C\u7684\u6B4C\u66F2</span></div><p>\u8BF4\u4EC0\u4E48&quot;\u67AF\u71E5\u4E4F\u5473&quot;\u5E76\u65E0\u6D4E\u4E8E\u4E8B\u2014\u2014\u56E0\u4E3A\u6211\u4EEC\u8FD0\u6C14\u4E0D\u597D\uFF0C\u5C31\u51FA\u751F\u5728\u5386\u53F2\u7684\u8FD9\u4E2A\u821E\u53F0\u3002</p><p>\u4E00\u76F4\u5230\u4E8C\u5341\u4E8C\u4E16\u7EAA\uFF0C\u6211\u4EEC\u90FD\u4F1A\u6BCF\u5929\u65E9\u4E0A\u4E03\u70B9\u949F\u8D77\u5E8A\uFF0C\u7136\u540E\u4E0A\u5B66\u6216\u4E0A\u73ED\uFF0C\u53CD\u590D\u8BF4\u4E00\u4E9B\u6BEB\u65E0\u91CD\u70B9\u7684\u8BDD\u3002\u5728\u5B66\u6821\uFF0C\u4E00\u6B21\u53C8\u4E00\u6B21\u4E0D\u65AD\u7684\u80CC\u82F1\u6587\u5355\u5B57\u3001\u5386\u53F2\u5E74\u53F7\uFF1B\u5728\u516C\u53F8\u5219\u4E00\u9762\u8BF4\u4E9B&quot;\u65E0\u804A\u900F\u4E86&quot;\u4E4B\u7C7B\u7684\uFF0C\u5374\u53C8\u4EE5\u4E00\u661F\u671F\u3001\u4E00\u4E2A\u6708\u3001\u4E00\u5E74\u7684\u5468\u671F\uFF0C\u53CD\u590D\u597D\u51E0\u4E2A\u661F\u671F\u3001\u597D\u51E0\u4E2A\u6708\u3001\u597D\u51E0\u5E74\u7684\u505A\u5B9E\u9645\u4E0A\u771F\u6B63\u65E0\u804A\u7684\u5DE5\u4F5C\u3002\u6B65\u8C03\u7F13\u7F13\u7684\u6700\u5148\u8FDB\u4EA7\u54C1\u4E0D\u65AD\u51FA\u73B0\uFF0C\u6B65\u8C03\u7F13\u7F13\u7684\u653F\u6CBB\u5BB6\u7EE7\u7EED\u8D2A\u6C61\uFF0C\u7535\u89C6\u5185\u5BB9\u6B65\u8C03\u7F13\u7F13\u7684\u7EE7\u7EED\u6EDA\u52A8\u3002\u4F46\u662F\uFF0C\u5F53\u5173\u6389\u7535\u89C6\u73AF\u987E\u56DB\u5468\u65F6\uFF0C\u5374\u53C8\u662F\u4E00\u5982\u5F80\u5E38\u7684\u53E6\u4E00\u4E2A\u6BCF\u4E00\u5929\u3002(\u5524\u9192&quot;\u5173\u6389\u7535\u89C6\u540E\u90A3\u79CD\u5947\u5999\u7684\u9ED1\u6697&quot;\uFF0C\u6B63\u662F\u8FD9\u672C\u4E66\u7684\u53E6\u4E00\u4E2A\u76EE\u6807\u3002)</p><p>\u4E09\u5C9B\u7531\u7EAA\u592B\u5728\u4ED6\u7684\u81EA\u4F20\u5C0F\u8BF4\u300A\u5047\u9762\u7684\u544A\u767D\u300B\u4E2D\uFF0C\u63D0\u5230:&quot;\u65E5\u5E38\u751F\u6D3B&quot;\u6BD4\u6218\u4E89\u8FD8\u8981\u6050\u6016\u3002\u6211\u4EEC\u603B\u662F\u4E00\u5FCD\u518D\u5FCD\u7684\u8FC7\u7740\u8FD9\u79CD&quot;\u4EE4\u4EBA\u98A4\u6296\u7684\u6050\u6016\u65E5\u5E38\u751F\u6D3B&quot;\uFF0C\u4E3A\u7684\u662F\u80FD\u5E26\u6765\u83AB\u540D\u5176\u5999\u7684&quot;\u5B89\u5B9A\u5C06\u6765&quot;\u3002\u6211\u4EEC\u4E00\u8DEF\u4E0A\u6B65\u6B65\u4E3A\u8425\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u7684\u907F\u514D\u65B9\u5411\u6709\u6240\u504F\u5DEE\uFF0C\u5E76\u4E14\u6CA1\u6709\u50CF\u7535\u89C6\u8FDE\u7EED\u5267\u90A3\u6837\u559C\u5267\u6536\u573A\u7684\u7ED3\u5C3E\u3002\u53EA\u662F\uFF0C\u5947\u602A\u7684&quot;\u559C\u60A6&quot;\u603B\u662F\u4E0D\u65AD\u6B65\u8C03\u7F13\u7F13\u7684\u7EE7\u7EED\u7740\u3002\u662F\u7684\uFF01\u5173\u952E\u8BCD\u662F&quot;\u6B65\u8C03\u7F13\u7F13&quot;\u548C&quot;\u53CD\u590D&quot;\u3002\u6301\u7EED\u7684\u76F8\u540C\u4E8B\u7269\u6B65\u8C03\u7F13\u6162\u7684\u53CD\u590D\u51FA\u73B0\uFF1B\u8FD9\u662F\u8BA9\u60F3\u6B7B\u7684\u60C5\u7EEA\u81A8\u80C0\u7684\u7B2C\u4E00\u8981\u7D20\u3002</p><div class="tooltip"><h2>\u300AAnother Brick in the Wall\u300B</h2><span class="tooltiptext">\u6307\u82F1\u56FD\u6447\u6EDA\u4E50\u56E2Pink Floyd\u7684\u6B4C\u66F2</span></div><p>\u4E00\u4E5D\u4E03\u516B\u5E74\u65E5\u672C\u66FE\u7ECF\u53D1\u751F\u4E00\u4EF6&quot;\u7EB8\u4E0A\u8FF7\u5BAB\u81EA\u6740\u4E8B\u4EF6&quot;\u3002</p><p>\u4E00\u5BF9\u4F4F\u5728\u65E5\u672C\u5BCC\u5C71\u53BF\u7684\u9AD8\u4E00\u5B6A\u751F\u59CA\u59B9\uFF0C\u88AB\u53D1\u73B0\u5728\u6811\u6797\u5185\u4E0A\u540A\u81EA\u6740\u8EAB\u4EA1\u3002\u5176\u4E2D\u4E00\u4EBA\u7684\u7B14\u8BB0\u672C\u4E0A\uFF0C\u753B\u6709\u7528\u56DB\u6761\u7AD6\u7EBF\u548C\u6570\u6761\u6A2A\u7EBF\u4EA4\u9519\u5F62\u6210\u7684\u7EB8\u4E0A\u8FF7\u5BAB\u56FE\u6848(\u4E00\u79CD\u6CBF\u7740\u7EBF\u5BFB\u627E\u6E90\u5934\u7684\u6E38\u620F)\u3002\u56FE\u6848\u4E0B\u9762\uFF0C\u5206\u522B\u5199\u7740&quot;\u65E5\u672C\u4EBA\u7684X&quot;\u3001&quot;\u81EA\u6740&quot;\u3001&quot;ROS&quot;\u3001&quot;\u5FA1\u4E09\u5BB6&quot;\u8FD9\u51E0\u4E2A\u4EE4\u4EBA\u65E0\u6CD5\u7406\u89E3\u7684\u5B57\u773C\u3002\u4ECE\u4E0A\u9762\u753B\u7684\u7EBF\u662F\u4E00\u76F4\u8FDE\u63A5\u5230&quot;\u81EA\u6740&quot;\u7684\u5730\u65B9\u7684\u3002\u7531\u4E8E\u5176\u7236\u6BCD\u600E\u4E48\u4E5F\u65E0\u6CD5\u60F3\u51FA\u5979\u4EEC\u81EA\u6740\u7684\u52A8\u673A\uFF0C\u6240\u4EE5\u7ED3\u8BBA\u662F\u4E24\u4EBA\u56E0\u4E3A&quot;\u8FF7\u5BAB&quot;\u800C\u81EA\u6740\u3002\u636E\u8BF4&quot;ROS&quot;\u4E5F\u8BB8\u662F\u6EDA\u52A8\u7684\u77F3\u5934 (Rolling Stones)\u7684\u610F\u601D\u3002\u56E0\u4E3A\u4E0A\u9762\u8FD8\u5199\u7740&quot;\u65E5\u672C\u4EBA\u7684X&quot;\u3001&quot;\u8BA8\u538C\u4E9A\u6D32\u4EBA&quot;\u7B49\u5B57\uFF0C\u6240\u4EE5\u8BF4\u4E0D\u5B9A\u4E0E\u6B64\u6709\u5173\u3002\u4E0D\u8FC7\uFF0C\u6211\u4EEC\u59CB\u7EC8\u65E0\u6CD5\u4E86\u89E3\u8FD9\u4E9B\u5B57\u7684\u610F\u601D\u3002\u4ECE\u524D\uFF0C\u6709\u4E2A\u6CD5\u5B98\u8BF4:&quot;\u4EBA\u7684\u751F\u547D\u6BD4\u5730\u7403\u8FD8\u91CD\u3002&quot;\u7136\u800C\uFF0C\u8FD9\u662F\u6781\u65E0\u4EF7\u503C\u7684\u8BEF\u89E3\u3002\u6B63\u5982\u540C\u4E03\u5341\u5E74\u4EE3\u7684\u90A3\u4E24\u4F4D\u9AD8\u4E2D\u5973\u751F\u65E9\u5DF2\u5BDF\u89C9\u7684\u4E00\u822C\uFF0C\u4EBA\u7684\u751F\u547D\u5F88\u8F7B\uFF0C\u548C&quot;\u65E5\u672C\u4EBA\u7684X&quot;\u4EE5\u53CA&quot;ROS&quot;\u4E00\u6837\u8F7B\u3002\u4E94\u5341\u5E74\u4EE3\u672B\u671F\uFF0C\u7F8E\u56FD\u5927\u4F17\u793E\u4F1A\u5B66\u8005\u8BF4\u8FC7:&quot;\u5927\u4F17\u5C31\u50CF\u9677\u5165\u65E0\u529B\u611F\u7684\u866B\u3002&quot;\u4E03\u5341\u5E74\u4EE3\u672B\u671F\uFF0C\u82F1\u56FD\u7684\u6447\u6EDA\u4E50\u56E2\u5531\u7684&quot;\u6211\u4EEC\u662F\u5899\u58C1\u4E2D\u7684\u4E00\u5757\u7816&quot;\u975E\u5E38\u8D70\u7EA2\u3002\u5373\u4F7F\u662F\u8FDB\u5165\u4E5D\u5341\u5E74\u4EE3\uFF0C\u65E5\u672C\u7684\u8FD9\u4E2A\u72B6\u51B5\u4F9D\u7136\u6CA1\u6709\u4EFB\u4F55\u6539\u53D8\u3002</p><p>\u8001\u6837\u5B50\uFF0C\u6211\u4EEC\u4ECD\u65E7\u662F\u5899\u58C1\u4E2D\u7684\u4E00\u5757\u7816\u2014\u2014\u8BC1\u636E\u662F\uFF0C\u5047\u8BBE\u6211\u4EEC\u4E4B\u4E2D\u6709\u4EFB\u4F55\u4E00\u4E2A\u4EBA\u6B7B\u4E86\uFF0C\u5FC5\u5B9A\u4F1A\u6709\u67D0\u4E2A\u4EBA\u6765\u53D6\u800C\u4EE3\u4E4B\u3002\u6CA1\u6709\u4E00\u4E2A\u4EBA\u7684\u5B58\u5728\u662F\u65E0\u53EF\u66FF\u6362\u7684\uFF0C\u4E5F\u6CA1\u6709\u4E00\u4E2A\u653F\u6CBB\u5BB6\u662F\u8DB3\u4EE5&quot;\u88AB\u6697\u6740&quot;\u7684\u3002\u53EA\u5C11\u4E86\u4E00\u5757\u7816\uFF0C\u5899\u58C1\u5E76\u4E0D\u4F1A\u56E0\u6B64\u800C\u5012\u584C\u3002</p><p>\u6211\u4EEC\u6BCF\u4E2A\u4EBA\u90FD\u5145\u6EE1\u65E0\u529B\u611F\uFF0C\u5B58\u5728\u6216\u4E0D\u5B58\u5728\u90FD\u65E0\u6240\u8C13\uFF0C\u6362\u53E5\u8BDD\u8BF4\uFF0C\u751F\u547D\u662F\u8F7B\u7684\uFF1B\u8FD9\u662F\u8BA9\u60F3\u6B7B\u7684\u60C5\u7EEA\u81A8\u80C0\u7684\u7B2C\u4E8C\u8981\u7D20\u3002</p><div class="tooltip"><h2>\u300AClockwork Orange\u300B</h2><span class="tooltiptext">\u6307\u7535\u5F71\u300A\u53D1\u6761\u6A59\u300B\uFF0C\u662F\u4E00\u90E8\u72AF\u7F6A\u7247</span></div><p>\u62B1\u7740\u8FD9\u79CD\u65E0\u529B\u611F\uFF0C\u6B65\u8C03\u7F13\u7F13\u7684\u53CD\u590D\u505A\u76F8\u540C\u4E8B\u60C5\u7684\u6211\u4EEC\uFF0C\u4E00\u70B9\u4E00\u70B9\u5FD8\u6389&quot;\u771F\u6B63\u6D3B\u7740\u7684\u771F\u5B9E\u611F&quot;\u3002\u5DF2\u7ECF\u6E10\u6E10\u5FD8\u4E86\u81EA\u5DF1\u662F\u6D3B\u7740\u8FD8\u662F\u6B7B\u4E86\u3002\u4F60\u6709\u611F\u89C9\u81EA\u5DF1\u662F&quot;\u6D3B\u7740&quot;\u5417?\u73B0\u5728\uFF0C\u751F\u4E0E\u6B7B\u4E4B\u95F4\uFF0C\u53EA\u88AB\u4E00\u6761\u7EC6\u5F97\u51E0\u4E4E\u770B\u4E0D\u89C1\u7684\u754C\u7EBF\u9694\u5F00\u800C\u5DF2\u3002</p><p>\u56E0\u6B64\uFF0C&quot;\u751F\u547D\u5F88\u91CD\u8981\uFF0C\u6240\u4EE5\u4E0D\u53EF\u4EE5\u81EA\u6740\u3002&quot;\u3001&quot;\u53EA\u8981\u80FD\u6D3B\u7740\uFF0C\u4E00\u5207\u90FD\u4F1A\u6709\u8F6C\u673A\u3002&quot;\u3001&quot;\u56E0\u4E3A\u5468\u906D\u7684\u4EBA\u4F1A\u96BE\u8FC7\uFF0C\u6240\u4EE5\u5FC5\u987B\u6D3B\u7740\u3002&quot;\u8FD9\u7C7B\u7684\u8BDD\uFF0C\u5DF2\u88AB\u6253\u5165\u51B7\u5BAB\uFF0C\u4E0D\u518D\u5177\u6709\u4EFB\u4F55\u8BF4\u670D\u529B\u3002\u5236\u6B62\u81EA\u6740\u7684\u6709\u6548\u8BDD\u8BED\uFF0C\u5DF2\u7ECF\u6D88\u5931\uFF1B\u5F15\u5BFC\u81EA\u6740\u7684\u4FE1\u53F7\u5DF2\u7ECF\u51FA\u73B0\u3002</p><p>\u662F\u7684\uFF0C\u8981\u6B7B\u4E5F\u53EF\u4EE5\u3002\u5982\u679C\u4E0A\u73ED\u6216\u4E0A\u5B66\uFF0C\u6D3B\u7740\u5F88\u4E0D\u8212\u670D\u7684\u8BDD\uFF0C\u5F88\u65E0\u8DA3\u7684\u8BDD\uFF0C\u751A\u81F3\u8FD8\u5F88\u75DB\u82E6\u7684\u8BDD\uFF0C\u662F\u53EF\u4EE5\u8DE8\u8D8A\u7EC6\u5F97\u5FEB\u770B\u4E0D\u89C1\u7684\u754C\u7EBF\u53BB\u5BFB\u6C42\u6B7B\u4EA1\uFF0C\u4EFB\u8C01\u90FD\u65E0\u6CD5\u52A0\u4EE5\u963B\u6B62\u3002</p><p>\u524D\u9762\u4E5F\u8BF4\u8FC7\uFF0C\u53CD\u6B63\u6D3B\u7740\uFF0C\u4E00\u5207\u4E5F\u4E0D\u4F1A\u6709\u6240\u6539\u53D8\u3002\u867D\u7136\u4E0D\u5177\u6709\u7279\u5F02\u529F\u80FD\uFF0C\u4E0D\u8FC7\u5927\u81F4\u4E0A\u53EF\u4EE5\u77E5\u9053\uFF0C\u4ECA\u540E\u7684\u793E\u4F1A\u6216\u81EA\u5DF1\u4F1A\u53D1\u751F\u4EC0\u4E48\u6837\u7684\u4E8B\u3002&quot;\u5C06\u6765\uFF01\u5C06\u6765\uFF01&quot;\uFF0C\u5C31\u7B97\u8FD9\u53E5\u8BDD\u518D\u600E\u4E48\u6709\u8BF4\u670D\u529B\u90FD\u6CA1\u7528\u3002\u4F60\u7684\u4EBA\u751F\uFF0C\u5927\u6982\u662F\u5728\u51FA\u751F\u5730\u5FF5\u5C0F\u5B66\u548C\u4E2D\u5B66\uFF0C\u4E0A\u8865\u4E60\u73ED\u4E3A\u8054\u8003\u8BFB\u4E66\uFF0C\u7136\u540E\u8FDB\u5165\u4E00\u6240\u9AD8\u4E2D\u6216\u5927\u5B66\u5C31\u8BFB\uFF0C\u6D51\u6D51\u5669\u5669\u73A9\u4E86\u56DB\u5E74\u540E\u8FDB\u5165\u67D0\u5BB6\u516C\u53F8\u4E0A\u73ED\u5DE5\u4F5C\u3002\u7537\u6027\u7684\u8BDD\u4E8C\u5341\u6765\u5C81\u4E09\u5341\u5C81\u524D\u7ED3\u5A5A\uFF0C\u9694\u5E74\u751F\u5B50\uFF0C\u51E0\u6B21\u7684\u5DE5\u4F5C\u5F02\u52A8\u6216\u5347\u8FC1\uFF0C\u6700\u9AD8\u5347\u5230\u7ECF\u7406\u804C\u4F4D\uFF0C\u516D\u5341\u5C81\u9000\u4F11\uFF0C\u4E4B\u540E\u7684\u5341\u5E74\u6216\u4E8C\u5341\u5E74\u8FC7\u7740\u4EAB\u53D7\u81EA\u5DF1\u5174\u8DA3\u7684\u751F\u6D3B\uFF0C\u6700\u540E\u6B7B\u4EA1\u3002\u9876\u591A\u5C31\u662F\u8FD9\u6837\u3002\u800C\u4E14\uFF0C\u4EE4\u4EBA\u7EDD\u671B\u7684\uFF0C\u8FD9\u7ADF\u662F\u6700\u80FD\u8BA9\u4EBA\u5B89\u5FC3\u7684\u7406\u60F3\u4EBA\u751F\u3002</p><p>\u5728\u8FD9\u6837\u7684\u72B6\u51B5\u4E0B\uFF0C\u5E73\u51E1\u5730\u6D3B\u7740\u5DF2\u7ECF\u518D\u4E5F\u6CA1\u4EC0\u4E48\u91CD\u5927\u610F\u4E49\u4E86\u3002\u8FD9\u6837\u4E0D\u662F&quot;\u771F\u7684\u6D3B\u7740&quot;\uFF0C\u6216\u8BB8\u53EA\u662F\u50CF\u505A\u70E4\u9E21\u7528\u7684\u5AE9\u9E21\u4E00\u6837\uFF0C&quot;\u88AB\u7ED9\u4E88\u751F\u547D\u6D3B\u7740&quot;\u800C\u5DF2\u3002\u6240\u4EE5\u5728\u9002\u5F53\u7684\u5730\u65B9\u4E3A\u4EBA\u751F\u753B\u4E0A\u4F11\u6B62\u7B26\uFF0C\u5E76\u4E0D\u4F1A\u6709&quot;\u60B2\u4F24\u4E0D\u5DF2&quot;\u3001&quot;\u4E0D\u4F1A\u53D1\u751F\u7B2C\u4E8C\u6B21&quot;\u3001&quot;\u62C5\u5FC3\u4F1A\u51FA\u73B0\u6CE2\u53CA\u6548\u5E94&quot;\u8FD9\u7C7B\u7684\u95EE\u9898\u3002</p><p>\u81EA\u6740\u5E94\u662F\u76F8\u5F53\u79EF\u6781\u7684\u884C\u4E3A\u3002</p><h2>&quot;Angel Dust&quot;</h2><p>\u6211\u6709\u4E00\u4E2A\u670B\u53CB\uFF0C\u4ED6\u6709\u4E00\u79CD\u53EB<ins class="tooltip">&quot;\u5929\u4F7F\u5C18\u57C3&quot;<span class="tooltiptext">\u5929\u4F7F\u5C18\u57C3(Angel Dust)\uFF0C\u5373\u73AF\u82EF\u5229\u5B9A(Phencyclidine,PCP)\uFF0C\u4E00\u79CD\u53EF\u4EE5\u5F3A\u70C8\u7684\u5F15\u8D77\u5E7B\u89C9\u3001\u6539\u53D8\u795E\u5FD7\u7684\u836F\u7269\u3002\u53EF\u4EE5\u5BFC\u81F4\u66B4\u529B\u884C\u4E3A\u3001\u660F\u8FF7\u3001\u766B\u75EB\u7B49\uFF0C\u4E00\u65E6\u4F7F\u7528\u5C06\u5F15\u53D1\u5BF9\u5927\u8111\u4E0D\u53EF\u9006\u7684\u635F\u5BB3\u3002<a href="https://en.wikipedia.org/wiki/Phencyclidine">\u7EF4\u57FA\u767E\u79D1\u9875\u9762(EN)</a></span></ins>\u7684\u836F\u7269\uFF0C\u8FD9\u662F\u4E00\u79CD\u5403\u4E86\u4E4B\u540E\u5934\u8111\u4F1A\u53D8\u5F97\u660F\u660F\u6C89\u6C89\uFF0C\u53EF\u4EE5\u6BEB\u4E0D\u5728\u610F\u5730\u4ECE\u9AD8\u697C\u5F80\u4E0B\u8DF3\u7684\u5F3A\u70C8\u836F\u7269\u3002\u4ED6\u628A\u8FD9\u4E9B\u836F\u7269\u88C5\u5728\u91D1\u5C5E\u5C0F\u56CA\u4E2D\uFF0C\u505A\u6210\u9879\u94FE\uFF0C\u5F62\u5F71\u4E0D\u79BB\u7684\u5E26\u5728\u8EAB\u4E0A\u3002\u4ED6\u8BF4:&quot;\u5FC5\u8981\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u5403\u4E0B\u8FD9\u4E2A\u6765\u5BFB\u6B7B\u3002&quot;\u6211\u7684\u670B\u53CB\u6CA1\u6709\u56FA\u5B9A\u5DE5\u4F5C\uFF0C\u6BCF\u5929\u6E38\u624B\u597D\u95F2\uFF0C\u8FC7\u5F97\u975E\u5E38\u6109\u5FEB\u3002</p><p>\u5E0C\u671B\u8FD9\u672C\u4E66\u53EF\u4EE5\u6210\u4E3A\u90A3\u6761\u91D1\u5C5E\u5C0F\u56CA\u9879\u94FE\u3002</p><h1 id="\u7F16\u8005-\u8BD1\u8005rena\u7684\u524D\u8A00" tabindex="-1">\u7F16\u8005&amp;\u8BD1\u8005Rena\u7684\u524D\u8A00 <a class="header-anchor" href="#\u7F16\u8005-\u8BD1\u8005rena\u7684\u524D\u8A00" aria-hidden="true">#</a></h1><p class="tooltip"><i><b>&quot;Another Thing&quot;</b></i><span class="tooltiptext">Another Thing\u8282\u7531Rena\uFF08<a href="https://github.com/YuriMiller">YuriMiller</a>\uFF09\u7F16\u5199\u3002\u5979\u5B8C\u6210\u4E86\u7FFB\u8BD1\u548C\u6821\u5BF9\u5DE5\u4F5C\uFF0C\u5E76\u53C2\u4E0E\u4E86\u5927\u90E8\u5206\u6CE8\u91CA\u7F16\u8BD1\uFF0C\u4E8E2018\u5E74\u7559\u4E0B\u672C\u4E66\u4E66\u7A3F\u4F5C\u4E3A&quot;\u6700\u540E\u7684\u793C\u7269&quot;\u540E\u81EA\u6740\u3002</span></p><p>\u5728\u6FD2\u4E34\u7EDD\u671B\uFF0C\u5BFB\u627E\u81EA\u6740\u7684\u65B9\u6CD5\u65F6\u5076\u7136\u53D1\u73B0\u4E86\u8FD9\u672C\u4E66\uFF0C\u4E8E\u662F\u5BF9\u5B83\u6DF1\u6DF1\u7740\u8FF7\u3002\u56E0\u4E3A\u8FD9\u672C\u4E66\u7684\u7279\u6B8A\u6027\u548C\u5E74\u4EE3\u7684\u4E45\u8FDC\uFF0C\u8D39\u52B2\u5468\u6298\u624D\u627E\u5230\u8FD9\u4EFD\u5C3D\u7BA1\u4E0D\u5B8C\u5168\u4F46\u5F25\u8DB3\u73CD\u8D35\u7684\u4E2D\u6587\u8BD1\u672C\u3002&quot;\u5B8C\u6210\u8FD9\u672C\u4E66\u7684\u7FFB\u8BD1\u548C\u6821\u5BF9\uFF01&quot;\u662F\u6211\u5728\u6700\u540E\u65F6\u523B\u7684\u6700\u540E\u5FC3\u613F\uFF0C\u770B\u7740\u4E66\u672C\u7531\u6B8B\u7F3A\u8D8B\u5411\u5B8C\u6574\u7684\u53D8\u5316\uFF0C\u5FC3\u91CC\u4E5F\u662F\u6EE1\u6EE1\u7684\u6210\u5C31\u611F\uFF0C\u4E5F\u80FD\u5B89\u7136\u79BB\u53BB\u4E86\u3002</p><p>\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u8FD9\u672C\u4E66\u4E2D\u5BF9\u8F83\u5E38\u7528\u7684\u70E7\u70AD\u81EA\u6740\u7684\u65B9\u6CD5\u5E76\u672A\u63D0\u53CA\uFF0C\u6240\u4EE5\u6211\u79C1\u81EA\u52A0\u5165\u4E86\u70E7\u70AD\u81EA\u6740\u7684\u5C0F\u8282(\u5728&quot;\u74E6\u65AF\u4E2D\u6BD2&quot;\u7AE0\u4E2D)\u548C\u5176\u4ED6\u7684\u4E00\u4E9B\u5185\u5BB9\u3002\u6709\u4EBA\u80FD\u5BF9\u8FD9\u672C\u4E66\u52A0\u4EE5\u5B8C\u5584\uFF0C\u6211\u60F3\u8FD9\u662F\u4F5C\u8005\u6240\u652F\u6301\u7684\u3002\u6B64\u5916\uFF0C\u968F\u7740\u65F6\u4EE3\u7684\u66F4\u66FF\uFF0C\u5728&quot;\u836F\u7269&quot;\u4E00\u7AE0\u4E2D\u63D0\u5230\u5F88\u591A\u836F\u7269(\u5C24\u5176\u662F\u6EB4\u5316\u7269\u7C7B\u548C\u5DF4\u6BD4\u59A5\u7C7B)\u5DF2\u88AB\u6BD2\u6027\u76F8\u5BF9\u66F4\u4F4E\u7684\u7269\u8D28\u53D6\u4EE3\u4E14\u5BF9\u8FD9\u4E9B\u836F\u7269\u7684\u76D1\u7BA1\u8D8B\u4E8E\u4E25\u683C\u3002\u4F46\u6211\u4ECD\u5C3D\u529B\u5C06\u8FD9\u4E9B\u836F\u7269\u7684\u6210\u5206\u5217\u51FA\u5E76\u52A0\u4EE5\u6CE8\u91CA\uFF0C\u5E0C\u671B\u80FD\u5E2E\u52A9\u9700\u8981\u7684\u4EBA\u3002</p><p>\u56E0\u4E3A\u8D2B\u7620\u7684\u533B\u5B66\u548C\u836F\u5B66\u77E5\u8BC6\u548C\u7F16\u6821\u65F6\u4EBA\u5DF2\u5904\u4E8E\u6DF7\u4E71\u548C\u5D29\u6E83\u8FB9\u7F18\uFF0C\u4E66\u4E2D\u5185\u5BB9\u96BE\u514D\u6709\u8C2C\u8BEF\u548C\u4E0D\u5B8C\u5584\u4E4B\u5904\uFF0C\u671B\u89C1\u8C05\u3002</p><p>\u6700\u540E\uFF0C\u611F\u8C22\u6B64\u4E66\u7E41\u4F53\u4E2D\u6587\u7248\u672C\u7684\u8BD1\u8005\u548C\u5728\u6B64\u8FC7\u7A0B\u4E2D\u63D0\u4F9B\u5E2E\u52A9\u7684\u4EBA\uFF0C\u6CA1\u6709\u4F60\u4EEC\u6CA1\u6709\u4ECA\u5929\u7684\u4E00\u5207\u3002</p><p align="right"><b>(Rena,2018-8-5)</b></p><h1 id="\u7F16\u8005nameless\u7684\u524D\u8A00" tabindex="-1">\u7F16\u8005Nameless\u7684\u524D\u8A00 <a class="header-anchor" href="#\u7F16\u8005nameless\u7684\u524D\u8A00" aria-hidden="true">#</a></h1><p class="tooltip"><i><b>&quot;2021\u5E74\u4FEE\u8BA2\u6309&quot;</b></i><span class="tooltiptext">\u8FD9\u4E00\u90E8\u5206\u7531<a href="https://github.com/Nameless-284">Nameless-284</a>\u7F16\u5199\uFF0C\u5728Rena\u544A\u522B\u4E16\u754C\u540E\uFF0CNameless\u63A5\u624B\u4E86\u8FD9\u672C\u4E66\u7684\u5B8C\u5584\u5DE5\u4F5C</span></p><p>\u9996\u5148\u8877\u5FC3\u795D\u613FRena\u80FD\u771F\u6B63\u5F97\u5230\u6C38\u6052\u7684\u5E73\u9759\uFF0C\u7B11\u7740\u67AF\u840E\uFF0C\u4E0D\u60E7\u5760\u843D\u3002</p><p>\u7531\u4E8E\u672C\u4E66\u7684\u7279\u6B8A\u6027\u548C\u5E74\u4EE3\u4E45\u8FDC\uFF0C\u672C\u4E66\u6240\u7528\u7684\u53C2\u8003\u6587\u732E\u5DF2\u4E0D\u53EF\u8003\u3002</p><p>\u8BE5\u7A3F\u662F\u4EE5Rena\u7684\u4E66\u7A3F\u4E3A\u57FA\u7840\uFF0C\u7ED3\u5408Issues\u677F\u5757\u7684\u4E8C\u5341\u591A\u6761\u53CD\u9988\u8FDB\u884C\u4FEE\u8BA2\u5199\u6210\u7684\uFF0C\u611F\u8C22\u6240\u4EE5\u5BF9\u6B64\u4E66\u63D0\u4F9B\u5E2E\u52A9\u7684\u4EBA\uFF0C\u4E5F\u611F\u8C22Rena\uFF0C\u662F\u4F60\u4EEC\u8BA9\u8FD9\u672C\u4E66\u53D8\u5F97\u5B8C\u6574\u3002</p><p>\u6211\u5C0A\u91CD\u90A3\u4E9B\u7ECF\u8FC7\u614E\u91CD\u8003\u8651\u9009\u62E9\u81EA\u6740\u7684\u4EBA\u505A\u51FA\u7684\u81EA\u6740\u51B3\u5B9A\uFF0C\u6B63\u5982\u539F\u4F5C\u8005\u76F8\u7530\u304F\u3072\u3092\u6240\u8BF4&quot;\u60F3\u6D3B\u7684\u8BDD\u5C31\u8981\u6D3B\u5F97\u81EA\u5728\uFF0C\u60F3\u6B7B\u7684\u8BDD\u4E5F\u8981\u6B7B\u5F97\u81EA\u5728&quot;</p><p>\u8BF4\u4E86\u4E00\u4E9B\u5E9F\u8BDD\uFF0C\u4E0B\u9762\uFF0C\u8FDB\u5165\u6B63\u9898\u5427\u3002</p><p align="right"><b>(Nameless,2021\u5E74\u7684<ins class="tooltip">\u67D0\u4E00\u5929<span class="tooltiptext">\u5927\u6982\u662F2021-5-10</span></ins>)</b></p><h1 id="\u7F16\u8005chenxiaosen-neo\u7684\u524D\u8A00" tabindex="-1">\u7F16\u8005Chenxiaosen-Neo\u7684\u524D\u8A00 <a class="header-anchor" href="#\u7F16\u8005chenxiaosen-neo\u7684\u524D\u8A00" aria-hidden="true">#</a></h1><p class="tooltip"><i><b>&quot;\u5199\u5728\u524D\u9762\u7684\u788E\u788E\u5FF5&quot;</b></i><span class="tooltiptext">\u8FD9\u4E00\u90E8\u5206\u7531<a href="https://github.com/Chenxiaosen-Neo">Chenxiaosen-Neo</a>\u7F16\u5199\uFF0C\u57FA\u4E8ERena\u7684\u7248\u672C\uFF0C\u8FDB\u884C\u4E86\u4FEE\u8BA2\u4E0E\u586B\u8865\uFF0C\u5E76\u5C06\u81EA\u5DF1\u7684\u81EA\u6740\u65B9\u5F0F\u8BE6\u5C3D\u5730\u5199\u5728\u4E66\u4E2D</span></p><p>\u6211\u4EEC\u4E0D\u80FD\u9009\u62E9\u662F\u5426\u6765\u5230\u8FD9\u4E2A\u4E16\u754C\uFF0C\u6211\u4EEC\u53EA\u80FD\u9009\u62E9\u662F\u5426\u79BB\u5F00\u3002\u5E76\u4E0D\u662F\u6240\u6709\u4EBA\u90FD\u90A3\u4E48\u7684\u5E78\u8FD0\uFF0C\u5728\u4E0D\u53EF\u9006\u8F6C\u7684\u60B2\u5267\u9762\u524D\uFF0C\u9009\u62E9\u5B89\u7136\u7684\u6B7B\u4EA1\u81F3\u5C11\u80FD\u8BA9\u5FC3\u7075\u4FDD\u7559\u6700\u540E\u7684\u5C0A\u4E25\u3002\u5411\u5982\u6211\u4E00\u6837\u60B2\u5267\u7684\u4EBA\u81F4\u4EE5\u9057\u61BE\u4E0E\u540C\u60C5\uFF0C\u613F\u901D\u8005\u6C38\u8FDC\u5B89\u7136\u3002\u4ECA\u5E74\u56DB\u6708\u6211\u4E5F\u8981\u51C6\u5907\u79BB\u5F00\u4E86\uFF0C\u6211\u540C\u76DB\u653E\u7684\u665A\u6A31\u5171\u751F\u6B7B\uFF0C\u771F\u662F\u4E0D\u9519\u7684\u65F6\u8282\u5462\u3002\u6211\u5BF9\u7F8E\u6709\u6240\u6267\u5FF5\uFF0C\u60F3\u4EE5\u5C3D\u53EF\u80FD\u7F8E\u7684\u65B9\u5F0F\u79BB\u5F00\uFF0C\u6240\u4EE5\u6211\u9009\u62E9\u70E7\u70AD\u4EA7\u751F\u4E00\u6C27\u5316\u78B3\u6765\u837C\u6BD2\u672C\u4F53\uFF0C\u5BF9\u7F8E\u6709\u8FFD\u6C42\u7684\u81EA\u6740\u8005\u53EF\u4EE5\u53C2\u8003\u6211\u7684\u6848\u5217\u3002</p><p>\u611F\u8C22Rena\uFF08YuriMiller\uFF09\u3001hskwhrk\u3001DL35Creative\u3001yangbowen\u4EE5\u53CA\u6240\u6709\u4E3A\u6B64\u4E66\u7FFB\u8BD1\u4EE5\u53CA\u5185\u5BB9\u5B8C\u5584\u505A\u51FA\u52AA\u529B\u7684\u4EBA\u3002\u6211\u5C06\u5BF9Issues\u677F\u5757\u7684\u53CD\u9988\u4EE5\u53CA\u5185\u5BB9\u8FDB\u884C\u8FDB\u4E00\u6B65\u4FEE\u8BA2\u3002\u6B64\u5916\uFF0C\u6211\u5BF9\u70E7\u70AD\u81EA\u6740\u90E8\u5206\u8FDB\u884C\u8FDB\u4E00\u6B65\u7684\u63A2\u8BA8\uFF0C\u5C06\u81EA\u5DF1\u7684\u81EA\u6740\u65B9\u5F0F\u8BE6\u7EC6\u5199\u5165\u4E66\u4E2D\u4F9B\u5927\u5BB6\u53C2\u8003\uFF0851\u9875\uFF09\uFF0C\u8BA9\u6211\u6700\u540E\u5FAE\u5149\u6E29\u6DA6\u9700\u8981\u5E2E\u52A9\u7684\u4EBA\u5427\u3002</p><p align="right"><b>(Chenxiaosen-Neo,2022\u5E744\u6708)</b></p><h1 id="markdown\u7248\u7F16\u8005needhourger\u7684\u524D\u8A00" tabindex="-1">Markdown\u7248\u7F16\u8005needhourger\u7684\u524D\u8A00 <a class="header-anchor" href="#markdown\u7248\u7F16\u8005needhourger\u7684\u524D\u8A00" aria-hidden="true">#</a></h1><p class="tooltip"><i><b>&quot;2022\u5E74&quot;</b></i><span class="tooltiptext">\u8FD9\u4E00\u90E8\u5206\u7531<a href="https://github.com/needhourger">needhourger</a>\u7F16\u5199\uFF0C\u5728Nameless\u518D\u7F16\u8F91\u540E\uFF0C\u5236\u4F5C\u4E86Markdown\u7248\u672C\uFF0C\u4EE5\u671F\u4FBF\u4E8E\u90E8\u7F72\u4E8E\u9759\u6001\u9875\u9762</span></p><p>\u5F88\u611F\u8C22\u4E4B\u524D\u4E24\u4F4D\u8BED\u8A00\u5927\u4F6C\u505A\u7684\u7FFB\u8BD1\uFF0C\u672C\u4EBA\u4E0D\u624D\uFF0C\u53EA\u80FD\u505A\u70B9\u673A\u68B0\u5F0F\u7684\u91CD\u590D\u52B3\u52A8\uFF0C\u5236\u4F5C\u4E86\u8FD9\u672C\u4E66\u7684\u7EAFMarkdown\u7248\u672C,\u53EF\u4EE5\u7ECF\u8FC7\u7B80\u5355\u7684\u4FEE\u6539\u6302\u5728\u5F88\u591A\u7EAF\u9759\u6001\u9875\u9762\u7684\u7F51\u7AD9\u4E0A\u3002\u8FD9\u672C\u4E66\u7684\u76EE\u7684\u7EDD\u4E0D\u662F\u6240\u8C13\u7684\u6002\u607F\u79BB\u5F00\u8FD9\u4E2A\u4E16\u754C\uFF0C\u6211\u89C9\u5F97\u539F\u4F5C\u8005\u5728\u540E\u8BB0\u4E2D\u5199\u7684\u5F88\u597D\uFF0C\u5E94\u8BE5\u628A\u540E\u8BB0\u548C\u524D\u8A00\u5408\u5728\u4E00\u8D77\u770B\u3002</p><blockquote><p>\u5728\u524D\u8A00\u4E2D\uFF0C\u5199\u4E86\u8FD1\u4F3C&quot;\u5173\u4E8E\u73B0\u4EE3\u793E\u4F1A\u4E0E\u81EA\u6740&quot;\u8FD9\u6837\u5938\u5F20\u7684\u5F00\u573A\u767D\uFF0C\u4E0D\u8FC7\u4E8B\u5B9E\u4E0A\u7684\u786E\u662F\u6709\u70B9\u8A00\u8FC7\u5176\u5B9E\u3002</p><p>\u5199\u8FD9\u672C\u4E66\u7684\u6700\u521D\u7406\u7531\uFF0C\u53EA\u662F\u56E0\u4E3A\u538C\u5026\u4E86&quot;\u4E0D\u53EF\u4EE5\u81EA\u6740&quot;\u8FD9\u4E2A\u600E\u4E48\u60F3\u90FD\u60F3\u4E0D\u51FA\u6709\u4EFB\u4F55\u6839\u636E\u7684\u89C2\u5FF5\uFF0C\u5374\u53D7\u5230\u5927\u4F17\u975E\u5E38\u5355\u7EAF\u7684\u4FE1\u4EFB\u3002</p><p>\u5C0F\u5B66\u8001\u5E08\u7528&quot;\u751F\u547D\u7684\u91CD\u8981&quot;\u505A\u9898\u76EE\u8BF7\u5B66\u751F\u5199\u4F5C\u6587\u7684\u60C5\u5F62\u5230\u5904\u53EF\u89C1\uFF1B\u81EA\u6740\u7684\u4EBA\u968F\u968F\u4FBF\u4FBF\u88AB\u8BF4\u662F\u610F\u5FD7\u529B\u8584\u5F31\u7B49\u7B49\u3002&quot;\u8981\u6D3B\u5F97\u575A\u5F3A&quot;\u8FD9\u79CD\u8BDD\u8F7B\u6613\u5C31\u80FD\u8BF4\u51FA\u53E3\u7684\u793E\u4F1A\uFF0C\u4E0D\u4EC5\u5C01\u95ED\u5F97\u4EE4\u4EBA\u5598\u4E0D\u8FC7\u6C14\u6765\uFF0C\u800C\u4E14\u4E5F\u4EE4\u4EBA\u6D3B\u5F97\u75DB\u82E6\u3002\u56E0\u6B64\uFF0C\u8BA9\u8FD9\u672C\u4E66\u6D41\u901A\uFF0C\u521B\u51FA&quot;\u4E07\u4E0D\u5F97\u5DF2\u65F6\uFF0C\u4E5F\u53EF\u4EE5\u5BFB\u6B7B&quot;\u7684\u9009\u62E9\uFF0C\u5E0C\u671B\u5728\u5C01\u95ED\u4E14\u8D70\u5230\u672B\u8DEF\u7684\u793E\u4F1A\u5F00\u8F9F\u4E00\u4E2A\u901A\u98CE\u53E3\uFF0C\u4F7F\u7A7A\u6C14\u6D41\u901A\uFF0C\u4F7F\u751F\u6D3B\u66F4\u5BB9\u6613\u4E9B\uFF0C\u8FD9\u624D\u662F\u6211\u771F\u6B63\u7684\u76EE\u7684\u3002</p><p>\u6211\u4E0D\u662F\u5728\u9610\u8FF0&quot;\u5927\u5BB6\u90FD\u6765\u81EA\u6740\u5427\uFF01&quot;\u8FD9\u6837\u65E0\u804A\u7684\u4E8B\uFF0C\u60F3\u6D3B\u7684\u8BDD\u5C31\u8981\u6D3B\u5F97\u81EA\u5728\uFF0C\u60F3\u6B7B\u7684\u8BDD\u4E5F\u8981\u6B7B\u5F97\u81EA\u5728\uFF0C\u751F\u547D\u5E94\u8BE5\u5C31\u662F\u8FD9\u4E48\u4E00\u56DE\u4E8B\u3002</p><p>\u5728\u6B64\uFF0C\u8981\u5411\u8D1F\u8D23\u7F16\u8F91\u7684\u843D\u5408\u7F8E\u6C99\u3001\u8D1F\u8D23\u7F8E\u672F\u8BBE\u8BA1\u7684\u94C3\u672C\u6210\u4E00\u53CA\u63D2\u753B\u7684\u6C99\u8FBE\u5E0C\u7F57\u5361\u5179\u8BFA\u5229\uFF0C\u81F4\u4E0A\u6DF1\u6DF1\u7684\u8C22\u610F\u3002</p></blockquote><p align="right"><b>(needhourger,2022.2.15)</b></p><h1 id="vitepress\u7248\u7F16\u8005\u5561\u5496\u7684\u524D\u8A00" tabindex="-1">Vitepress\u7248\u7F16\u8005\u5561\u5496\u7684\u524D\u8A00 <a class="header-anchor" href="#vitepress\u7248\u7F16\u8005\u5561\u5496\u7684\u524D\u8A00" aria-hidden="true">#</a></h1><p class="tooltip"><i><b>&quot;\u8DDD\u79BB\u9AD8\u8003\u6B63\u597D100\u5929\u5F00\u59CB\u7684 \u2018\u4E00\u65F6\u5174\u8D77\u2019 &quot;</b></i><span class="tooltiptext">\u8FD9\u4E00\u90E8\u5206\u7531<a href="https://github.com/fkx4-p">fkx4-p</a>\u7F16\u5199\uFF0C\u5728needhourger\u5236\u4F5CMarkdown\u7248\u672C\u540E\uFF0C\u56E0\u4E0D\u6EE1\u610F\u5176\u6837\u5F0F\u800C\u4E00\u65F6\u5174\u8D77\u5236\u4F5C\u7684Vitepress\u7248\u672C</span></p><p>\u611F\u8C22GitHub\uFF0C\u4E5F\u611F\u8C22\u6700\u521D\u7684\u7E41\u4F53\u8BD1\u8005\uFF0CRena\u3001Nameless\u7684\u518D\u7F16\u8F91\u4EE5\u53CAChenxiaosen-Neo\u5BF9\u70E7\u70AD\u81EA\u6740\u90E8\u5206\u8FDB\u884C\u7684\u8FDB\u4E00\u6B65\u7684\u63A2\u8BA8\uFF0C\u4F7F\u5F97\u8FD9\u672C\u4E66<i>&quot;\u8DDF\u5F97\u4E0A\u65F6\u4EE3&quot;</i>\u3002</p><p>\u6211\u5728\u9AD8\u8003\u767E\u65E5\u8A93\u5E08\u7684\u4E0A\u5348(2-25)\u6FD2\u4E34\u7EDD\u671B\u7684\u65F6\u5019\u627E\u5230\u4E86\u8FD9\u672C\u73CD\u8D35\u7684\u4E66\uFF0C\u5E76\u5BF9\u5176\u7740\u8FF7\u4E0D\u5DF2\u3002\u800C\u524D\u8BD1\u8005\u4E0E\u524D\u7F16\u8005\u7684\u90E8\u5206\u9519\u8BEF\u8BA9\u6211\u840C\u751F\u4E86\u4E00\u4E9B\u60F3\u6CD5\u3002&quot;\u628A\u5B83\u91CD\u65B0\u4FEE\u8BA2\uFF01&quot;\u6210\u4E3A\u4E86\u6211\u6700\u5927\u7684\u5FC3\u613F\uFF0C\u4E5F\u6210\u4E3A\u4E86\u6211\u8D70\u4E0B\u53BB\u7684\u52A8\u529B\u3002\u770B\u7740\u9519\u8BEF\u4E00\u4E2A\u4E2A\u88AB\u4FEE\u6B63\uFF0C\u5FC3\u91CC\u611F\u53D7\u5230&quot;\u6211\u7EC8\u4E8E\u4E5F\u53EF\u4EE5\u4E3A\u8FD9\u4E2A\u4E16\u754C\u7559\u4E0B\u70B9\u4EC0\u4E48\u4E86&quot;\uFF0C\u8FD9\u6837\u4E5F\u53EF\u4EE5\u65E0\u61BE\u5730\u8D70\u4E86\u3002</p><p>\u5982\u679C\u770B\u5230\u4E86\u8FD9\u672C\u4E66\uFF0C<em><strong>\u90A3\u4E48\u8BF7\u628A\u5B83\u8BFB\u5B8C\u5427\u3002</strong></em></p><p>\u8FD9\u4E2A\u7248\u672C\u662F\u57FA\u4E8ENameless\u7684pdf\u7248\u8FDB\u884C\u91CD\u7F16\u8F91\u7684\u3002\u5728\u8FD9\u4E2A\u7248\u672C\u4E2D\uFF0C\u6211\u4F1A\u5BF9\u4E00\u4E9B\u6CE8\u91CA\u6DFB\u52A0\u5916\u94FE\uFF0C\u4FEE\u6539\u4E00\u4E9B\u53EF\u4EE5\u786E\u5B9A\u9519\u8BEF\u7684\u6CE8\u91CA\uFF0C\u5E76\u5C06\u4E00\u4E9B\u6210\u5206\u7684\u97F3\u8BD1\u540D\u79F0\u66FF\u6362\u4E3A\u5176\u5316\u5B66\u540D\u79F0\u3002\u6211\u8FD8\u4F1A\u5BF9\u6587\u7AE0\u4E2D\u4E0D\u901A\u987A\u7684\u5730\u65B9\u8FDB\u884C\u4FEE\u6539\u3002\u4E0D\u8FC7\uFF0C\u672C\u4EBA\u8BED\u6587\u6781\u5176\u5DEE\u52B2\uFF0C\u5E76\u4E14\u56E0\u4E3A\u4E00\u4E9B\u539F\u56E0\uFF0C\u5934\u8111\u4F1A\u51FA\u73B0\u65F6\u65AD\u65F6\u7EED\u7684\u6DF7\u4E71\uFF0C\u53EF\u80FD\u5BF9\u6587\u7AE0\u9020\u6210\u7834\u574F\uFF0C\u5982\u679C\u4ECB\u610F\uFF0C\u8BF7\u79FB\u6B65<a href="https://github.com/YuriMiller/CompleteSuicideManual-Zh_CN" target="_blank" rel="noopener noreferrer">Rena\u7684\u7248\u672C</a>\u3001<a href="https://github.com/Nameless-284/Completely_Suicide_Manual" target="_blank" rel="noopener noreferrer">Nameless\u7684\u7248\u672C</a>\u6216\u662F<a href="https://github.com/Chenxiaosen-Neo/CompleteSuicideManual2022" target="_blank" rel="noopener noreferrer">Chenxiaosen-Neo\u7684\u7248\u672C</a>\uFF0C\u6291\u6216\u662F<a href="https://github.com/needhourger/Completely_Suicide_Manual" target="_blank" rel="noopener noreferrer">needhourger\u7684\u7248\u672C</a>\u3002</p><p>\u6211\u7684\u4E00\u70B9\u70B9\u81EA\u79C1\u7684\u8BF7\u6C42\uFF1A\u5982\u679C\u770B\u5230\u4E86\u8FD9\u672C\u4E66\uFF0C\u5982\u679C\u53EF\u4EE5\u7684\u8BDD\uFF0C\u8BF7\u4E0D\u8981\u4EE5\u4EFB\u4F55\u65B9\u5F0F\u3001\u5F62\u5F0F\u5C06\u5176\u4F20\u64AD\u7ED9\u4E0D\u5FC5\u8981\u7684\u4EBA\u3002\u5982\u679C\u53EF\u4EE5\uFF0C\u8BF7\u4E0D\u8981Star/Fork/Watch\u8FD9\u4E2Arepo\u4EE5\u53CA\u5176\u5B83\u6240\u6709\u6709\u5173\u6B64\u4E66\u7684repo\u3002\u8C22\u8C22\u3002</p><p>\u6700\u540E\uFF0C\u611F\u8C22\u82CF\u82CF\uFF0C\u4ED6\u5BF9\u6211\u4E0D\u79BB\u4E0D\u5F03\uFF0C\u59CB\u7EC8\u5728\u5E2E\u52A9\u6211\u3001\u529D\u6170\u6211\uFF0C\u505A\u6211\u7684\u7CBE\u795E\u652F\u67F1\u3002\u5982\u679C\u6CA1\u6709\u4ED6\uFF0C\u6211\u6216\u8BB8\u65E9\u5C31\u4EE5\u66F4\u52A0\u75DB\u82E6\u7684\u65B9\u5F0F\u6B7B\u53BB\u4E86\u3002</p><p>\u53EF\u80FD\u60A8\u5DF2\u7ECF\u611F\u5230\u538C\u70E6\u4E86\uFF0C\u90A3\u4E48\u6211\u4EEC\u5C31\u8FDB\u5165\u6B63\u9898\u5427\u3002</p><p align="right"><b>(\u5561\u5496,2022-2-27)</b></p>',68),q=[p];function s(n,i,r,h,l,c){return e(),o("div",null,q)}var g=t(a,[["render",s]]);export{b as __pageData,g as default};
