import{_ as t,c as o,o as u,a as q}from"./app.a7049be0.js";const l='{"title":"\u56E0\u7232\u6211\u662F\u672C\u7248\u7DE8\u8005\u6240\u4EE5\u53EF\u4EE5\u52A0\u5728\u524D\u9762\u7684\u5C0F\u8A18","description":"","frontmatter":{},"headers":[],"relativePath":"index.md"}',e={},p=q('<h1 id="\u56E0\u7232\u6211\u662F\u672C\u7248\u7DE8\u8005\u6240\u4EE5\u53EF\u4EE5\u52A0\u5728\u524D\u9762\u7684\u5C0F\u8A18" tabindex="-1">\u56E0\u7232\u6211\u662F\u672C\u7248\u7DE8\u8005\u6240\u4EE5\u53EF\u4EE5\u52A0\u5728\u524D\u9762\u7684\u5C0F\u8A18 <a class="header-anchor" href="#\u56E0\u7232\u6211\u662F\u672C\u7248\u7DE8\u8005\u6240\u4EE5\u53EF\u4EE5\u52A0\u5728\u524D\u9762\u7684\u5C0F\u8A18" aria-hidden="true">#</a></h1><blockquote><p><em>&quot;\u4EE5\u7232\u53EA\u8981\u5728\u6DF1\u591C\u6291\u5236\u547C\u865F \u4FBF\u503C\u5F97\u767B\u4E0A\u9AD8\u81FA\u6602\u9996\u7D22\u6C42\u5609\u734E&quot;</em></p></blockquote><p>\u6211\u66FE\u5929\u771F\u7684\u4EE5\u7232\uFF0C\u6211\u7684\u5FCD\u8010\u53EF\u4EE5\u63DB\u4F86\u548C\u5E73\uFF0C\u53EF\u4EE5\u8B93\u6211\u88AB\u7A31\u7232&quot;\u597D\u5B69\u5B50&quot;\uFF0C\u53EF\u662F\u6211\u7D42\u65BC\u767C\u73FE\uFF0C\u5FCD\u8010\u53EA\u6703\u63DB\u4F86\u66F4\u52A0\u66B4\u8650\u7684\u653B\u64CA\u3002\u4F46\u662F\u7576\u6211\u610F\u8B58\u5230\u9019\u4E00\u9EDE\u7684\u6642\u5019\uFF0C&quot;\u5974\u6027&quot;\u3001&quot;\u4F4E\u8072\u4E0B\u6C23&quot;\u5DF2\u7D93\u88AB\u523B\u5230\u4E86\u9AA8\u5B50\u88CF\u3002\u6211\u8207\u4ED6\u4EBA\u76F8\u540C\u7684\u884C\u7232\u88AB\u8A8D\u7232\u662F&quot;\u767C\u760B&quot;\uFF0C\u6211\u8207\u5176\u4ED6\u4EBA\u76F8\u540C\u7684\u8A71\u8A9E\u88AB\u8A8D\u7232\u662F&quot;\u5728\u6B3A\u8CA0\u4EBA&quot;\u3002\u7136\u800C\uFF0C\u6211\u537B\u5BE6\u5728\u6C92\u6709\u52C7\u6C23\u7206\u767C\u3002</p><blockquote><p><em>&quot;\u5982\u4ECA\u624D\u660E\u767D \u5B64\u7368\u5E76\u975E\u5B64\u7368\u7684\u5168\u8C8C&quot;</em></p><p><em>&quot;\u6700\u5F8C\u624D\u767C\u73FE \u6F2B\u9577\u4E5F\u50C5\u662F\u5B64\u7368\u4E00\u89D2&quot;</em></p></blockquote><p>\u5B64\u7368\u53EA\u662F&quot;\u5B64\u7368&quot;\u55CE\uFF1F\u2014\u2014\u4E0D\u662F\u7684\u3002\u6211\u9858\u610F\u7528\u67D0\u53E5\u540D\u8A00\u7684\u53CD\u8A71\u4F86\u5F62\u5BB9\u5B83\uFF1A&quot;\u6211\u5011\u6BCF\u500B\u4EBA\u90FD\u662F\u4E00\u5EA7\u5B64\u5CF6&quot;\u3002\u6211\u5011\u5B64\u55AE\u5730\u51FA\u751F\uFF0C\u6709\u7684\u4EBA\u6703\u5728\u4E00\u751F\u4E2D\u5411\u5176\u5B83\u5CF6\u5DBC\u5EFA\u9020\u6A4B\u6881\uFF0C\u800C\u6709\u4E9B\u4EBA\u6703\u4E0D\u65B7\u5730\u6BC0\u58DE\u6240\u6709\u67B6\u8A2D\u5230\u4E00\u534A\u7684\u6A4B\u6881\u3002\u6211\u5011\u8981\u77E5\u9053\uFF0C\u6211\u5011\u6240\u770B\u5230\u7684&quot;\u5B64\u7368&quot;\u53EA\u662F&quot;\u81EA\u5DF1\u611F\u53D7\u5230\u7684\u5B64\u7368&quot;\uFF0C\u800C\u6211\u5011\u7684&quot;\u5B64\u7368&quot;\uFF0C\u5728\u5225\u4EBA\u770B\u4F86\u5247\u662F&quot;\u53E6\u4E00\u7A2E\u60C5\u6CC1&quot;\u3002\u5177\u9AD4\u662F\u4EC0\u9EBD\u5462\uFF1F\u6211\u4E5F\u4E0D\u77E5\u9053\uFF0C\u56E0\u7232 <em>&quot;\u6211\u5011\u4EC0\u9EBD\u90FD\u4E0D\u77E5\u9053&quot;</em>\u3002</p><p>\u6F2B\u9577\u78BA\u5BE6\u4E5F\u50C5\u662F\u5B64\u7368\u7684\u4E00\u89D2\uFF0C\u5176\u4E0D\u662F&quot;\u5B64\u7368&quot;\u7684\u4E00\u5207\u4E2D\u6700\u4EE4\u4EBA\u75DB\u82E6\u7684\u4E8B\uFF0C\u4F46\u537B\u662F\u6700\u4EE4\u4EBA\u53D7\u6298\u78E8\u7684\u4E00\u4EF6\u3002\u6C92\u6709\u4EBA\u6709\u7FA9\u52D9\u8207\u4F60\u6210\u7232\u5925\u4F34\uFF0C\u6C92\u6709\u4EBA\u6709\u7FA9\u52D9\u5411\u4F60\u7684\u5CF6\u5DBC\u67B6\u8A2D\u6A4B\u6881\u3002</p><blockquote><p><em>&quot;\u82E6\u76E1\u540E\u7518\u4F86 \u662F\u4EC0\u9EBD\u8B93\u6211\u5982\u6B64\u81C6\u60F3&quot;</em></p><p><em>&quot;\u6C38\u9060\u4E0D\u8981\u76F8\u4FE1\u82E6\u96E3\u662F\u503C\u5F97\u7684\uFF0C\u82E6\u96E3\u5C31\u662F\u82E6\u96E3\uFF0C\u82E6\u96E3\u4E0D\u6703\u5E36\u4F86\u6210\u529F\uFF0C\u82E6\u96E3\u4E0D\u503C\u5F97\u8FFD\u6C42\uFF0C\u78E8\u7DF4\u610F\u5FD7\u662F\u56E0\u7232\u82E6\u96E3\u7121\u6CD5\u8EB2\u958B\u3002&quot;\u2014\u2014\u4F59\u83EF\u300A\u6D3B\u8457\u300B</em></p></blockquote><p>\u9019\u4E16\u754C\u4E0A\u54EA\u88CF\u6709&quot;\u4E00\u4EFD\u8015\u8018\uFF0C\u4E00\u4EFD\u6536\u7A6B&quot;\uFF1F\u9019\u4E9B\u6771\u897F\u53EA\u4E0D\u904E\u662F\u9A19\u5C0F\u5B69\u52AA\u529B\u7684\u7F77\u4E86\u3002\u7B49\u5230\u9577\u5927\u5F8C\uFF0C\u624D\u6703\u6709\u4EBA\u660E\u767D\uFF0C\u5341\u4EFD\u8015\u8018\uFF0C\u4E5F\u53EF\u4EE5\u9846\u7C92\u4E0D\u6536\u3002\u800C\u9019\u4E9B\u4EBA\u4FBF\u6703\u88AB\u770B\u4F5C\u662F&quot;\u5F31\u8005&quot;\u3001&quot;loser&quot;\uFF0C\u88AB\u6307\u8CAC\u70BA&quot;\u4E0D\u6C42\u4E0A\u9032&quot;\u3001&quot;\u5EE2\u7269&quot;\u7B49\u7B49\u3002</p><p>\u656C\u90A3\u4E9B\u6C92\u6709\u56E0\u5275\u50B7\u800C\u8B8A\u5F97\u66F4\u5F3A\u5927\u7684\u4EBA\u3001\u90A3\u4E9B\u8B8A\u5F97\u66F4\u52A0\u654F\u611F\u548C\u6C92\u6709\u5B89\u5168\u611F\u7684\u4EBA\u3001\u90A3\u4E9B\u8B8A\u5F97\u5BB9\u6613\u54ED\u6CE3\u7684\u4EBA\u3001\u90A3\u4E9B\u6703\u88AB\u4E00\u4E9B\u5C0F\u4E8B\u64CA\u57AE\u7684\u4EBA\u3002\u6211\u53D7\u5920\u4E86\u90A3\u7A2E&quot;\u5275\u50B7\u8B93\u4F60\u8B8A\u5F97\u66F4\u52A0\u5F3A\u5927&quot;\u7684\u8AAC\u6CD5\u3002\u6211\u5011\u53EA\u662F\u5E78\u5B58\u8005\uFF0C\u4E0D\u662F\u8D85\u7D1A\u82F1\u96C4\u3002</p><p>\u82E6\u76E1\u7518\u4F86\u7684\u4F8B\u5B50\u80AF\u5B9A\u662F\u6709\uFF0C\u56E0\u7232\u82E6\u76E1\u7518\u4F86\u8005\u90FD\u6703\u88AB\u7576\u4F5C\u6A21\u7BC4\uFF0C\u88AB\u4E00\u4E9B\u4EBA\u7528\u4F86&quot;\u52F8\u5C0E&quot;\u5225\u4EBA\uFF0C\u800C\u90A3\u4E9B\u82E6\u5230\u4E86\u751F\u547D\u7684\u76E1\u982D\u4E5F\u6C92\u6709\u7518\u7684\u4EBA\uFF0C\u5247\u6703\u88AB\u8846\u4EBA\u6240\u907A\u5FD8\uFF0C\u6700\u7D42\u6D88\u901D\u5728\u6B77\u53F2\u7684\u9577\u6CB3\u88CF\u3002\u800C\u6C92\u6709\u6210\u529F\u7684\u4EBA\uFF0C\u624D\u662F\u66F4\u591A\u7684\u90A3\u4E00\u90E8\u5206\u3002</p><blockquote><p><em>&quot;\u5F9E\u4F86\u6C92\u6709\u4EBA\u8A31\u8AFE\u6703\u7D66\u6211\u5011\u5929\u5802&quot;</em></p></blockquote><p>\u57FA\u7763\u7232\u6211\u5011\u8D16\u7F6A\u4E86\u55CE\uFF1F\u4ED6\u5927\u6982\u662F\u53EA\u8D16\u4E86\u751F\u65BC\u5176\u524D\uFF0C\u5373\u516C\u5143\u524D\u7684\u4EBA\u5011\u7684\u7F6A\u3002\u4FE1\u4EF0\u4EC0\u9EBD\u90FD\u4E0D\u6703\u4FDD\u8B49\u4F60&quot;\u9032\u5165\u5929\u5802&quot;\uFF0C\u7576\u7136\uFF0C\u7F6A\u5927\u60E1\u6975\u7684\u4EBA\u4E5F\u4E0D\u6703\u90FD\u88AB\u9063\u9001\u81F3\u5730\u7344\u3002\u5982\u679C\u8F2A\u56DE\u662F\u6B63\u78BA\u7684\u8A71\uFF0C\u90A3\u9EBD\u7232\u4EC0\u9EBD\u6211\u5011\u5F9E\u4F86\u6C92\u6709\u807D\u8AAC\u904E\u6709\u4EBA\u56DE\u61B6\u8D77&quot;\u4EE5\u524D\u7684\u4E8B&quot;\u5462\uFF1F\u6211\u5011\u6BCF\u500B\u4EBA\u90FD\u6C92\u6709\u7F6A\uFF0C\u53EA\u4E0D\u904E\u662F\u7D71\u6CBB\u8005\u7232\u4E86\u7D71\u6CBB\u4EBA\u6C11\u800C\u5C07&quot;\u4EBA\u751F\u800C\u6709\u7F6A&quot;\u52A0\u8AF8\u4E8E\u6211\u5011\u7F77\u4E86\u3002</p>',12),a=[p];function c(_,r,s,n,m,d){return u(),o("div",null,a)}var h=t(e,[["render",c]]);export{l as __pageData,h as default};
